import React, { useState } from "react";
import axios from "axios";
import { Card, CardContent } from "./components/card";
import { Button } from "./components/button";

const App = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    console.log("Selected file:", file);
    setSelectedFile(file);
    setPrediction(null);
    setError(null);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedFile) {
      setError("Please select a file before submitting.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    console.log("FormData content:");
    for (const pair of formData.entries()) {
      console.log(pair[0], pair[1]);
    }

    try {
      const response = await axios.post("http://localhost:8000/uploadfile/", formData);
      console.log("API Response:", response.data);
      setPrediction(response.data.resposta);
      setError(null);
    } catch (error) {
      console.error("Error uploading file:", error);
      setError("Failed to process the image. Please try again.");
    }
  };

  //1 entender e organizar o html e css da pagina - começando pelos componentes
  //2 

  return (
    <div className="container">
      <Card className="card">
        <CardContent>
          <h1 className="titulo">Upload File</h1>
          <form onSubmit={handleSubmit} className="formulario">
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="input-arquivo"
            />
            <Button type="submit" className="botao">
              Submit
            </Button>
          </form>

          {prediction !== null && (
            <div className="resultado">
              <p className="texto-resultado">Prediction: {prediction}</p>
            </div>
          )}

          {error && (
            <div className="erro">
              <p className="texto-erro">{error}</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default App;
