import React, { useState } from "react";
import axios from "axios";
import { Card, CardContent } from "./components/card";
import { Button } from "@/components/button";

const App = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setPrediction(null);
    setError(null);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedFile) {
      setError("Please select a file before submitting.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("http://localhost:8000/uploadfile/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setPrediction(response.data.resposta);
      setError(null);
    } catch (error) {
      console.error("Error uploading file:", error);
      setError("Failed to process the image. Please try again.");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <Card className="w-96 p-4 shadow-xl">
        <CardContent>
          <h1 className="text-xl font-bold mb-4">Captcha Recognition</h1>
          <form onSubmit={handleSubmit} className="flex flex-col gap-4">
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border file:border-gray-300 file:text-sm file:font-semibold file:bg-gray-100 hover:file:bg-gray-200"
            />
            <Button type="submit" className="w-full">
              Submit
            </Button>
          </form>

          {prediction !== null && (
            <div className="mt-4">
              <p className="text-green-600 font-bold">Prediction: {prediction}</p>
            </div>
          )}

          {error && (
            <div className="mt-4">
              <p className="text-red-600 font-bold">{error}</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default App;
